function onElement(element: { tagName: string; children: any; }) {
  // Remove the first two children of an ol tag.
  if (element.tagName === 'head' || element.tagName === 'nav') {
    let i = 0;
    for (const child of element.children) {
      // Children might be text node or comments.
      // We don't want to count these.
      if (isTag(child) && i < 2) {
        removeElement(child);
        i++;
      }
    }
  }
}

const domVisitors = {
  onElement: onElement
};
function ignoreDomNode(node: Node, parent: NodeWithChildren) {
  // remove anchors children of <p> elements
  return (
    isTag(node) && node.type === 'script' ? <></> : node
  );
}

const ignoredDomTags = ["style", "meta", "head", "script", "noscript", "link", "metadata"];

